<?php
$mpdfDirPath = realpath(__DIR__ . '');
if (file_exists($mpdfDirPath . '/mpdf/mpdf.php')) {
    require_once $mpdfDirPath . '/mpdf/mpdf.php';
} else {
    throw new Exception(
        sprintf(
            'Could not find file \'%s\'. It is generated by Composer. Use \'install --prefer-source\' or \'update --prefer-source\' Composer commands to move forward.',
            $mpdfDirPath . '/autoload.php'
        )
    );
}
class M_pdf
{
    public $pdf;

    public function __construct($mode = 'utf-8', $format = 'A4', $orientation = 'P', $default_font_size = 0, $default_font = '', $mgl = 10, $mgr = 10, $mgt = 10, $mgb = 10, $mgh = 6, $mgf = 3)
    {
        $this->pdf = new mPDF($mode, $format, $default_font_size, $default_font, $mgl, $mgr, $mgt, $mgb, $mgh, $mgf, $orientation);
    }

    public static function create_mPdf($default_font_size = 0, $default_font = '', $format = 'A4', $mode = 'utf-8', $mgl = 10, $mgr = 10, $mgt = 10, $mgb = 10, $mgh = 6, $mgf = 3, $orientation = 'P')
    {
        return new mPDF($mode, $format, $default_font_size, $default_font, $mgl, $mgr, $mgt, $mgb, $mgh, $mgf, $orientation);
    }
}